import "./Testimonials.css";
import { Swiper, SwiperSlide } from "swiper/react";
import profile1 from "../../img/profile1.jpg";
import profile2 from "../../img/profile2.jpg";
import profile3 from "../../img/profile3.jpg";
import profile4 from "../../img/profile4.jpg";
import { Pagination } from "swiper";
import "swiper/css/pagination";
import "swiper/css";
const Testimonials = () => {
  const clients = [
    {
      img: profile1,
      review:
        "1Lorem ipsum dolor sit amet. Aut natus recusandae qui facere nesciunt ut error molestias qui deleniti labore est consequatur quia non dolor minus ex repellat dolores? Nam voluptate cumque aut voluptas amet sed dolorem incidunt est assumenda incidunt ut enim consequuntur rem assumenda eius rem dolorem eaque.",
    },
    {
      img: profile2,
      review:
        "2Lorem ipsum dolor sit amet. Aut natus recusandae qui facere nesciunt ut error molestias qui deleniti labore est consequatur quia non dolor minus ex repellat dolores? Nam voluptate cumque aut voluptas amet sed dolorem incidunt est assumenda incidunt ut enim consequuntur rem assumenda eius rem dolorem eaque.",
    },
    {
      img: profile3,
      review:
        "3Lorem ipsum dolor sit amet. Aut natus recusandae qui facere nesciunt ut error molestias qui deleniti labore est consequatur quia non dolor minus ex repellat dolores? Nam voluptate cumque aut voluptas amet sed dolorem incidunt est assumenda incidunt ut enim consequuntur rem assumenda eius rem dolorem eaque.",
    },
    {
      img: profile4,
      review:
        "4Lorem ipsum dolor sit amet. Aut natus recusandae qui facere nesciunt ut error molestias qui deleniti labore est consequatur quia non dolor minus ex repellat dolores? Nam voluptate cumque aut voluptas amet sed dolorem incidunt est assumenda incidunt ut enim consequuntur rem assumenda eius rem dolorem eaque.",
    },
  ];
  return (
    <section className="testimonials">
      <div className="p-heading">
        <span>Clients always get</span>
        <span>Exceptional work</span>
      </div>
      {/* slider */}
      <Swiper
        modules={[Pagination]}
        slidesPerView={1}
        pagination={{ clickable: true }}
      >
        {clients.map((client, index) => {
          return (
            <SwiperSlide key={index}>
              <div>
                <img src={client.img} alt="" />
                <p>{client.review}</p>
              </div>
            </SwiperSlide>
          );
        })}
      </Swiper>
    </section>
  );
};

export default Testimonials;
